echo ""

# R√©cup√©rer le token
TOKEN=$(curl -k -s -X POST https://54.38.146.213/api/v0/auth_extended \
    -H "Content-Type: application/json" \
    -d '{"username":"cheick","password":"cheick2025"}' \
    | grep -o '"token":"[^"]*"' | cut -d'"' -f4)

if [ -z "$TOKEN" ]; then
    echo "‚ùå Impossible d'obtenir le token"
    exit 1
fi

# Compter les labs depuis l'API CML
CML_LABS_COUNT=$(curl -k -s -H "Authorization: Bearer $TOKEN" \
    "https://54.38.146.213/api/v0/labs?show_all=true" \
    | grep -o '"[a-f0-9-]\{36\}"' | wc -l)

echo "üì° Serveur CML:"
echo "   Nombre de labs: $CML_LABS_COUNT"
echo ""

# Stats depuis la base de donn√©es
echo "üíæ Base de donn√©es locale:"
php artisan tinker --execute="
echo '   Labs en DB: ' . \App\Models\Lab::count() . PHP_EOL;
echo '   Reservations: ' . \App\Models\Reservation::count() . PHP_EOL;
echo '   Reservations actives: ' . \App\Models\Reservation::where('status', 'active')->where('end_at', '>', now())->count() . PHP_EOL;
echo '   Reservations compl√©t√©es: ' . \App\Models\Reservation::where('status', 'completed')->count() . PHP_EOL;
" 2>/dev/null

echo ""
echo "üìä Comparaison:"
echo "   CML API: $CML_LABS_COUNT labs"
DB_LABS=$(php artisan tinker --execute="echo \App\Models\Lab::count();" 2>/dev/null | tail -1)
echo "   Base de donn√©es: $DB_LABS labs"
echo ""

if [ "$CML_LABS_COUNT" -gt 0 ] && [ "$DB_LABS" -eq 0 ]; then
    echo "‚ö†Ô∏è  Les labs ne sont pas synchronis√©s dans la base de donn√©es locale"
    echo "   L'application affiche les labs depuis l'API CML en temps r√©el"
    echo "   Pour synchroniser, utilisez: php artisan sync:cml-labs"
fi


